<template>
  <div class="flow-container" ref="container"></div>
</template>

<script lang="ts" setup>
import LogicFlow from '@logicflow/core'
import { DndPanel, SelectionSelect, Menu } from '@logicflow/extension'
import '@logicflow/core/lib/style/index.css'
import '@logicflow/extension/lib/style/index.css'
import { reactive, toRefs, ref, onMounted } from 'vue'
const emits = defineEmits(['onClickEle'])

const lf = ref<LogicFlow>()
const container = ref()
interface Props {
  dndList: any[]
  data: LogicFlow.GraphConfigData
}
const props = withDefaults(defineProps<Props>(), {
  dndList: () => [
    {
      type: 'circle',
      text: '开始',
      label: '开始节点',
      icon: 'data:image/png;base64,'
    },
    {
      type: 'rect',
      label: '用户任务',
      icon: 'data:image/png;base64,',
      className: 'important-node'
    }
  ]
})
const { dndList, data } = toRefs(props)
const onClickElement = (data: any) => {
  emits('onClickEle', data)
}
onMounted(() => {
  LogicFlow.use(DndPanel)
  LogicFlow.use(SelectionSelect)
  LogicFlow.use(Menu)
  lf.value = new LogicFlow({
    container: container.value,
    grid: true
  })
  //@ts-ignore
  lf.value.extension.dndPanel.setPatternItems(dndList.value)
  //@ts-ignore
  lf.value.extension.menu.setMenuConfig({
    nodeMenu: [
      {
        text: '删除',
        callback(node: any) {
          lf.value!.deleteNode(node.id)
        }
      }
    ],
    edgeMenu: [
      {
        text: '删除',
        callback(edge: any) {
          lf.value!.deleteEdge(edge.id)
        }
      }
    ],
    graphMenu: false
  })
  lf.value.render(data.value)
  lf.value.on('node:click', onClickElement)
})
const updateNodeData = (data: any) => {
  lf.value?.getNodeModelById(data.id)?.setProperties({
    extraData: data.properties.extraData
  })
}
const getRawData = () => {
  return lf.value?.getGraphRawData()
}
defineExpose({ getRawData, updateNodeData })
</script>
<style scoped lang="scss">
.flow-container {
  width: 100%;
  height: 100%;
}
</style>
